import starRed from "./assets/red.png";
import starGrey from "./assets/grey.png";
import starGold from "./assets/yellow.png";
import Rating from "react-rating";
import { isDisabled } from "@testing-library/user-event/dist/utils";
import { useState } from "react";
import { getAuthToken } from "../utils/auth";
import { useSubmit } from "react-router-dom";

const CategoryList = ({ categories }) => {
    const [showForm, setShowForm] = useState(false);
    const [selectedCategoryTitle, setSelectedCategoryTitle] = useState("");
    const [newCategory, setNewCategory] = useState("");

    const onChangeCategoryHandler = (event) => {
        setNewCategory(event.target.value)
    }

    const submitCategoryHandler = async (id) => {
        const token = getAuthToken();

        const response = await fetch(`http://localhost:8000/category`, {
            method: "POST",
            headers: {
                Authorization: "Bearer " + token,
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ title: newCategory }),
        })

        categories.push(await response.json());//auto load, for geting id for delete and edit
        setShowForm(false);

    };

    const submit = useSubmit();

    const deleteCategoryHandler = async (id) => {
        const token = getAuthToken();
        const proceed = window.confirm("Are you sure?");
       

        try {
            const response = await fetch(`http://localhost:8000/category/${id}`, {
                method: "DELETE",
                headers: {
                    Authorization: "Bearer " + token,
                    "Content-Type": "application/json",
                },
            })

            
            if (!response.ok) {
                throw new Error("Failed to delete category");
            }
            categories = categories.filter((category) => category._id !== id);

            if (proceed) {
                submit(null, { method: "delete" });
            }
        } catch (error) {
            console.error("Error deleting category:", error.message);
        }

    }


    return (
        <>
            <ul>
                <h1>Categories: </h1>
                {
                    categories.map((cat) => {

                        return (
                            <li style={{ padding: "8px 16px", borderBottom: "1px solid #ddd", listStyleType: "none" }}>
                                <div> {cat.title}</div>
                                <button style={{ backgroundColor: "orange" }} >Edit</button>
                                <button style={{ backgroundColor: "red" }} onClick={() => { { deleteCategoryHandler() } }}>Delete</button>

                            </li>)
                    })

                }
                <button style={{ backgroundColor: "green" }}
                    onClick={() => {
                        setShowForm(!showForm)
                    }}>New</button>
                {showForm && (<>
                    <input name="title" id="title" onChange={onChangeCategoryHandler} ></input>
                    <button onClick={() => { submitCategoryHandler() }} style={{ color: "green" }}>Submit</button>
                </>)}
            </ul>

        </>
    )
}

export default CategoryList;
