import { Form, json, redirect, useActionData, useNavigate } from "react-router-dom";
import BookForm from "../components/BookForm";
import { getAuthToken } from "../utils/auth";
import Modal from "react-modal";
import { Zoom } from "react-reveal";
import { useEffect, useState } from "react";

import React from 'react'


import { loadStripe } from "@stripe/stripe-js";////22
import { CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import axios from "axios";
import { useRouteLoaderData } from "react-router-dom";

const CARD_OPTIONS = {
	iconStyle: "solid",
	style: {
		base: {
			iconColor: "#c4f0ff",
			color: "#fff",
			fontWeight: 500,
			fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
			fontSize: "16px",
			fontSmoothing: "antialiased",
			":-webkit-autofill": { color: "#fce883" },
			"::placeholder": { color: "#87bbfd" }
		},
		invalid: {
			iconColor: "#ffc7ee",
			color: "#ffc7ee"
		}
	}
}


const CardForm = ({ reservations }) => {
    let actionData = useActionData();
    const stripe = useStripe();
    const elements = useElements();
    const [success, setSuccess] = useState(false)
  

    const propertyData = useRouteLoaderData("property-detail");
    const [formData, setFormData] = useState({
      number: '',
      name: '',
      expiry: '',
      cvc: ''
    });

    const handleReset = () => {
        setFormData({
          number: '',
          name: '',
          expiry: '',
          cvc: ''
        });
        console.log("ok")
      };
    
      const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
      };
    


















  
  
  
    const handlePaymentSubmit = async (e) => {
      e.preventDefault()
      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: "card",
        card: elements.getElement(CardElement)
      })
  
      if (!error) {
        try {
          const { id } = paymentMethod
          const response = await axios.post("http://localhost:4000/payment", {
            amount: actionData.price,
            id
          })
  
          if (response.data.success) {
            console.log("Successful payment")
            setSuccess(true)
          }
  
        } catch (error) {
          console.log("Error", error)
        }
      } else {
        console.log(error.message)
      }
    }
  
  
    return(
        <>
        
                  <form>
                      <br />
                      <br />
                      <br />
                      <h1>🏁 Enter Card details</h1>
                      <a href="https://dashboard.stripe.com/test/developers">
                        Stripe
                      </a>
                      <iframe
                        className="card2024"
                        src="https://my.spline.design/creditcard-d88eb15d40c59aa8fee2992a1fccf38b/"
                        width={2000}
                        height={450}>
                      </iframe>
                      <div >
                        <h2>Total:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{actionData.price} {propertyData.property.currency}</h2>
                        <li style={{ borderBottom: "1px solid #ddd", listStyleType: "none" }} />
                        <br />
                      </div>
                      <div>
                        <label>Card Number&nbsp;</label>
                        <input
                          name="number"
                          value={formData.number}
                          component="input"
                          type="text"
                          onChange={handleInputChange}
                          pattern="[\d| ]{16,22}"
                          placeholder="1234 1234 1234 1234"
                        />
                      </div>(" ")
                      <div>
                        <label>Full name&nbsp;</label>
                        <input
                          name="name"
                          value={formData.name}
                          component="input"
                          type="text"
                          onChange={handleInputChange}
                        />
                      </div>(" ")
                      <div>
                        <label>Card Expiration&nbsp;</label>
                        <input
                          name="expiry"
                          value={formData.expiry}
                          component="input"
                          type="text"
                          pattern="\d\d/\d\d"
                          placeholder="MM / YY"
                          onChange={handleInputChange}
                        />
                        <label>&nbsp;&nbsp;&nbsp;CVC&nbsp;</label>
                        <input
                          name="cvc"
                          value={formData.cvc}
                          component="input"
                          type="text"
                          pattern="\d{3,4}"
                          placeholder="CVC"
                          onChange={handleInputChange}
                        />
                      </div>(" ")


                      <fieldset className="FormGroup">
                        <div className="FormRow">
                          <CardElement options={CARD_OPTIONS} />
                        </div>
                      </fieldset>
                      <div className="buttons">
                        <button type="submit" style={{ backgroundColor: "blue" }} onClick={handlePaymentSubmit}>Pay</button>
                        <button
                          type="button"
                          onClick={handleReset}
                          style={{ backgroundColor: "grey" }}
                        >Reset</button>
                      </div>(" ")

                  </form>
                  :
                  <div>
                    <h2>You bought a property!</h2>
                  </div>
                }
        </>
    )
}

export default CardForm;
