import { Form, json, redirect, useActionData, useNavigate, useRouteLoaderData } from "react-router-dom";
import BookForm from "../components/BookForm";
import { getAuthToken } from "../utils/auth";
import Modal from "react-modal";
import { Zoom } from "react-reveal";
import { useEffect, useState } from "react";

import React from 'react'
import { render } from 'react-dom'
import Styles from './Styles'
import { Field } from 'react-final-form'
import Card from './Card'
import {
  formatCreditCardNumber,
  formatCVC,
  formatExpirationDate
} from './cardUtils'
import { loadStripe } from "@stripe/stripe-js";

const sleep = ms => new Promise(resolve => setTimeout(resolve, ms))//////////////////////////

const BookPage = () => {
  let actionData = useActionData();
  const [modalOpen, setModalOpen] = useState(true);
  const navigate = useNavigate();
  const propertyData = useRouteLoaderData("property-detail");/////////////////////
  const [formData, setFormData] = useState({
    number: '',
    name: '',
    expiry: '',
    cvc: ''
  });////////////////////////
 
  const closeModal = () => {
    setModalOpen(false);
    navigate("/");
  }
  ////////////////////////////////////////////////////////////////

  // useEffect(() => {
  //   if (!window.document.getElementById("stripe-script")) {
  //     var s = window.document.createElement("script");
  //     s.id = "stripe-script";
  //     s.type = "text/javascript";
  //     s.src = "https://js.stripe.com/v2/";
  //     s.onload = () => {
  //       window["Stripe"].setPublishableKey(
  //         "pk_test_51OlUi6HkXpQM0uUWV0hKxwxs7L8mFEasFk3i2LC2vq70KUkHJn1jxWBSDRef2Wlfjkk5wLIjWEK24HvLMrrOJ4r400Fi5eNK7N"
  //       );
  //     };
  //     window.document.body.appendChild(s);
  //   }
  // }, []);


  // const onSubmit = async (values) => {
  //   await sleep(300);
  //   try {
  //     window.Stripe.card.createToken(
  //       {
  //         number: values.number,
  //         exp_month: values.expiry.split("/")[0],
  //         exp_year: values.expiry.split("/")[1],
  //         cvc: values.cvc,
  //         name: values.name,
  //       },
  //       (status, response) => {
  //         if (status === 200) {
  //           axios
  //             .post("/stripe-payment", {
  //               token: response,
  //               email: values.email,
  //               amount: values.amount,
  //             })
  //             .then((res) => window.alert(JSON.stringify(res.data, 0, 2)))
  //             .catch((err) => console.log(err));
  //         } else {
  //           console.log(response.error.message);
  //         }
  //       }
  //     );
  //   } catch (error) { }
  // };


  const handleReset = () => {
    setFormData({
      number: '',
      name: '',
      expiry: '',
      cvc: ''
    });
    console.log("ok")
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  ///////////////////////////////////////////////////////

  let values;
  return (
    <>
      {actionData && !actionData.message &&
        <Modal
          isOpen={actionData.pay_type === "card" && actionData && !actionData.message && modalOpen}
          onRequestClose={closeModal}>
          <Zoom>
            <button style={{ backgroundColor: "red" }} onClick={closeModal}>
              x
            </button>

            <div className="order-details">







              {/* ///////////////////////////////////////////////////////////////////////////////////////////////////////////// */}
              <form>
              <Styles>
              <br/>
              <br/>
              <br/>
                <h1>🏁 Enter Card details</h1>
                <a href="https://dashboard.stripe.com/test/developers">
                  Stripe
                </a>
                <iframe
                  className="card2024"
                  src="https://my.spline.design/creditcard-d88eb15d40c59aa8fee2992a1fccf38b/"
                  width={2000}
                  height={450}>
                </iframe>
                <div >
                  <h2>Total:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 
                  &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{actionData.price} {propertyData.property.currency}</h2>
                  <li style={{ borderBottom: "1px solid #ddd", listStyleType: "none" }}/>
                  <br/>
                </div>
                <div>
                  <label>Card Number&nbsp;</label>
                  <input
                    name="number"
                    value={formData.number}
                    component="input"
                    type="text"
                    onChange={handleInputChange}
                    pattern="[\d| ]{16,22}"
                    placeholder="1234 1234 1234 1234"
                    format={formatCreditCardNumber}
                  />
                </div>(" ")
                <div>
                  <label>Full name&nbsp;</label>
                  <input
                    name="name"
                    value={formData.name}
                    component="input"
                    type="text"
                    onChange={handleInputChange}
                  />
                </div>(" ")
                <div>
                  <label>Card Expiration&nbsp;</label>
                  <input
                    name="expiry"
                    value={formData.expiry}
                    component="input"
                    type="text"
                    pattern="\d\d/\d\d"
                    placeholder="MM / YY"
                    onChange={handleInputChange}
                    format={formatExpirationDate}
                  />
                  <label>&nbsp;&nbsp;&nbsp;CVC&nbsp;</label>
                  <input
                    name="cvc"
                    value={formData.cvc}
                    component="input"
                    type="text"
                    pattern="\d{3,4}"
                    placeholder="CVC"
                    onChange={handleInputChange}
                    format={formatCVC}
                  />
                </div>(" ")
                <div className="buttons">
                  <button type="submit" style={{ backgroundColor: "blue" }}>Submit</button>
                  <button
                    type="button"
                    onClick={handleReset}
                    style={{ backgroundColor: "grey" }}
                  >Reset</button>
                </div>(" ")

              </Styles>
              </form>

              {/* ///////////////////////////////////////////////////////////////////////////////////////////////////////////// */}

            </div>
          </Zoom>
        </Modal>
      }




      {actionData && !actionData.message &&
        <Modal
          isOpen={actionData.pay_type === "cash" && actionData && !actionData.message && modalOpen}
          onRequestClose={closeModal}>
          <Zoom>
            <button style={{ backgroundColor: "red" }} onClick={closeModal}>
              x
            </button>

            <div className="order-details">
              <h3 className="success-message">Your order has been placed</h3>
              <h2>Order {actionData._id}</h2>
              <ul>
                <li>
                  <div>Name:</div>
                  <div>{actionData.nume}</div>
                </li>
                <li>
                  <div>Perioud:</div>
                  <div>{actionData.data_start} - {actionData.data_end}</div>
                </li>
                <li>
                  <div>Price:</div>
                  <div>{actionData.price}</div>
                </li>

              </ul>
            </div>
          </Zoom>
        </Modal>
      }






      <BookForm />
    </>
  );
};



//render(<BookPage/>, document.getElementById(""));





export const action = async ({ request, params }) => {
  const propertyId = params.propertyId;
  const data = await request.formData();

  const data_start = data.get("data_start");
  const data_end = data.get("data_end");
  // console.log(data_start, data_end, propertyId);


  let saleData = {
    placeId: propertyId,
    data_start: data.get("data_start"),
    data_end: data.get("data_end"),
    nume: data.get("nume"),
    adresa: data.get("adresa"),
    telefon: data.get("telefon"),
    pay_type: data.get("pay_type"),

  };

  const token = getAuthToken();

  const response = await fetch("http://localhost:8000/sale", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + token,
    },
    body: JSON.stringify(saleData),
  });


  if (response.status === 422 || response.status === 401) {
    return response;
  }

  if (!response.ok) {
    throw json({ message: "Could not authenticate user." }, { status: 500 });
  }

  return response;

}

export default BookPage;
