import { Form, useActionData, useNavigation } from "react-router-dom";
import classes from "./AuthForm.module.css";

const RecoverPasswordForm = () => {
  const data=useActionData();
    const navigation = useNavigation();
    const isSubmitting = navigation.state === "submitting";

     return (
    <>
      <Form method="post" className={classes.form}>
        <p>
          <label htmlFor="confirmResetToken">Reset token</label>
          <input id="confirmResetToken" type="confirmResetToken" name="confirmResetToken" required />
        </p>
        <p>
          <label htmlFor="newPassword">New Password</label>
          <input id="newPassword" type="newPassword" name="newPassword" required />
        </p>
        <p>
          <label htmlFor="repeatPassword">Repeat Password</label>
          <input id="repeatPassword" type="repeatPassword" name="repeatPassword" required />
        </p>
        <button disabled={isSubmitting}>
          {isSubmitting ? "Submitting" : "Save"}
        </button>
      </Form>
    </>
  );
}

export default RecoverPasswordForm;
