import { Form, json, redirect, useActionData, useNavigate } from "react-router-dom";
import BookForm from "../components/BookForm";
import { getAuthToken } from "../utils/auth";
import Modal from "react-modal";
import { Zoom } from "react-reveal";
import { useEffect, useState } from "react";

import React from 'react'
import { render } from 'react-dom'
import Styles from './Styles'
import { Field } from 'react-final-form'
import Card from './Card'
import {
  formatCreditCardNumber,
  formatCVC,
  formatExpirationDate
} from './cardUtils'


const BookPage = () => {
  let actionData = useActionData();
  const [modalOpen, setModalOpen] = useState(true);
  const navigate = useNavigate();

  console.log(actionData && !actionData.message)

  const closeModal = () => {
    setModalOpen(false);
    navigate("/");
  }
  //////////////////////
  const sleep = ms => new Promise(resolve => setTimeout(resolve, ms))

function BookPage(){
  useEffect(()=>{
    if(! window.document.getElementById("stripe-script")){
      var s=window.document.createElement("script")
      s.id="";
      s.type="";
      s.src="";
      s.onload=()=>{
        window["Stripe"].setP
      }
    }
  })
}

  const onSubmit = async values => {
    await sleep(300)
    window.alert(JSON.stringify(values, 0, 2))
  }
  ///////////////////////
  return (
    <>
      {actionData && !actionData.message &&
        <Modal
          isOpen={actionData.pay_type === "card" && actionData && !actionData.message && modalOpen}
          onRequestClose={closeModal}>
          <Zoom>
            <button style={{ backgroundColor: "red" }} onClick={closeModal}>
              x
            </button>

            <div className="order-details">








              <Styles>
                <h1>🏁 Enter Card details</h1>
                <a href="https://dashboard.stripe.com/test/developers">
                  Stripe
                </a>
                <Form
                  onSubmit={onSubmit}
                  render={({
                    handleSubmit,
                    form,
                    submitting,
                    pristine,
                    values,
                    active
                  }) => {
                    return (
                      <form onSubmit={handleSubmit}>
                        <Card
                          number={values.number || ''}
                          name={values.name || ''}
                          expiry={values.expiry || ''}
                          cvc={values.cvc || ''}
                          focused={active}
                        />
                        <div>
                          <Field
                            name="number"
                            component="input"
                            type="text"
                            pattern="[\d| ]{16,22}"
                            placeholder="Card Number"
                            format={formatCreditCardNumber}
                          />
                        </div>
                        <div>
                          <Field
                            name="name"
                            component="input"
                            type="text"
                            placeholder="Name"
                          />
                        </div>
                        <div>
                          <Field
                            name="expiry"
                            component="input"
                            type="text"
                            pattern="\d\d/\d\d"
                            placeholder="Valid Thru"
                            format={formatExpirationDate}
                          />
                          <Field
                            name="cvc"
                            component="input"
                            type="text"
                            pattern="\d{3,4}"
                            placeholder="CVC"
                            format={formatCVC}
                          />
                        </div>
                        <div className="buttons">
                          <button type="submit" disabled={submitting}>
                            Submit
                          </button>
                          <button
                            type="button"
                            onClick={form.reset}
                            disabled={submitting || pristine}
                          >
                            Reset
                          </button>
                        </div>
                        <h2>Values</h2>
                        <pre>{JSON.stringify(values, 0, 2)}</pre>
                      </form>
                    )
                  }}
                />
              </Styles>










            </div>
          </Zoom>
        </Modal>
      }




      {actionData && !actionData.message &&
        <Modal
          isOpen={actionData.pay_type === "cash" && actionData && !actionData.message && modalOpen}
          onRequestClose={closeModal}>
          <Zoom>
            <button style={{ backgroundColor: "red" }} onClick={closeModal}>
              x
            </button>

            <div className="order-details">
              <h3 className="success-message">Your order has been placed</h3>
              <h2>Order {actionData._id}</h2>
              <ul>
                <li>
                  <div>Name:</div>
                  <div>{actionData.nume}</div>
                </li>
                <li>
                  <div>Perioud:</div>
                  <div>{actionData.data_start} - {actionData.data_end}</div>
                </li>
                <li>
                  <div>Price:</div>
                  <div>{actionData.price}</div>
                </li>

              </ul>
            </div>
          </Zoom>
        </Modal>
      }






      <BookForm />
    </>
  );
};


export const action = async ({ request, params }) => {
  const propertyId = params.propertyId;
  const data = await request.formData();

  const data_start = data.get("data_start");
  const data_end = data.get("data_end");
  // console.log(data_start, data_end, propertyId);


  let saleData = {
    placeId: propertyId,
    data_start: data.get("data_start"),
    data_end: data.get("data_end"),
    nume: data.get("nume"),
    adresa: data.get("adresa"),
    telefon: data.get("telefon"),
    pay_type: data.get("pay_type"),

  };

  const token = getAuthToken();

  const response = await fetch("http://localhost:8000/sale", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + token,
    },
    body: JSON.stringify(saleData),
  });


  if (response.status === 422 || response.status === 401) {
    return response;
  }

  if (!response.ok) {
    throw json({ message: "Could not authenticate user." }, { status: 500 });
  }

  return response;

}

export default BookPage;
