import {
    Form, useNavigate,
} from "react-router-dom";

import classes from './AuthForm.module.css';

const BookForm = () => {

    const navigation = useNavigate();
    const isSubmitting = navigation.state === "submitting";
    return (
        <>
            <Form method="post" className={classes.form}>
                <h1>Book this property</h1>
                <p>
                    <label htmlFor="data_start">Data_start</label>
                    <input style={{backgroundColor:"black", color: "white"}} id="data_start" type="date" name="data_start" />
                </p>

                <p>
                    <label htmlFor="data_end">Data_end</label>
                    <input style={{backgroundColor:"black", color: "white"}} id="data_end" type="date" name="data_end" />
                </p>
               

                <div className={classes.actions}>

                    <button disabled={isSubmitting}>
                        {isSubmitting ? "Submitting" : "Save"}
                    </button>
                </div>
            </Form>
        </>
    );
};



export const action = async ({ request, params }) => {
    const method = request.method;
  
  
    const aaa = await request.formData();
  
    const data = Object.fromEntries(aaa);
  
    const formData = new FormData();
    formData.append("title", data.title);
    formData.append("suprafata", data.suprafata);
    formData.append("tara", data.tara);
    formData.append("oras", data.oras);
    formData.append("strada", data.strada);
    formData.append("judet", data.judet);
    formData.append("price", data.price);
    formData.append("currency", data.currency);
    formData.append("categoryId", data.category);
}

export default BookForm;
