import {
    Form, useActionData, useNavigate, useRouteLoaderData,
} from "react-router-dom";

import classes from './AuthForm.module.css';
import PropertyItem from "./PropertyItem";

const BookForm = () => {

    const navigation = useNavigate();
    const isSubmitting = navigation.state === "submitting";
    const data = useActionData();
    const propertyData = useRouteLoaderData("property-detail");
    
    return (
        <>
    <PropertyItem property={propertyData.property}/>
            <Form method="post" className={classes.form}>
                <h1>Book this property</h1>
                {data && data.message && <p>{data.message}</p>}
                {data && data.data && (
                    <>
                        {data.data.map((err) => {
                            console.log("---", err);
                            return (
                                <p key={err.msg}>{err.msg}</p>
                            )
                        })}
                    </>
                )}
                <p>
                    <label htmlFor="data_start">Data_start</label>
                    <input style={{ backgroundColor: "black", color: "white" }} id="data_start" type="date" name="data_start" />
                </p>

                <p>
                    <label htmlFor="data_end">Data_end</label>
                    <input style={{ backgroundColor: "black", color: "white" }} id="data_end" type="date" name="data_end" />
                </p>


                <div className={classes.actions}>

                    <button disabled={isSubmitting}>
                        {isSubmitting ? "Submitting" : "Checkout"}
                    </button>
                </div>
            </Form>
        </>
    );
};




export default BookForm;
