import { Form, json, redirect, useActionData, useNavigate, useRouteLoaderData } from "react-router-dom";
import BookForm from "../components/BookForm";
import { getAuthToken } from "../utils/auth";
import Modal from "react-modal";
import { Zoom } from "react-reveal";
import { useEffect, useState } from "react";

import React from 'react'
import { render } from 'react-dom'
import Styles from './Styles'
import { Field } from 'react-final-form'
import Card from './Card'
import {
  formatCreditCardNumber,
  formatCVC,
  formatExpirationDate
} from './cardUtils'

import { loadStripe } from "@stripe/stripe-js";////22
import { Elements } from "@stripe/react-stripe-js";///22
import { CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import axios from "axios";




const CARD_OPTIONS = {
	iconStyle: "solid",
	style: {
		base: {
			iconColor: "#c4f0ff",
			color: "#fff",
			fontWeight: 500,
			fontFamily: "Roboto, Open Sans, Segoe UI, sans-serif",
			fontSize: "16px",
			fontSmoothing: "antialiased",
			":-webkit-autofill": { color: "#fce883" },
			"::placeholder": { color: "#87bbfd" }
		},
		invalid: {
			iconColor: "#ffc7ee",
			color: "#ffc7ee"
		}
	}
}


const BookPage = () => {
  let actionData = useActionData();
  const [modalOpen, setModalOpen] = useState(true);
  const navigate = useNavigate();

  const propertyData = useRouteLoaderData("property-detail");/////////////////////
  const [formData, setFormData] = useState({
    number: '',
    name: '',
    expiry: '',
    cvc: ''
  });////////////////////////

  const closeModal = () => {
    setModalOpen(false);
    navigate("/");
  }
  ////////////////////////////////////////////////////////////////

  const stripe = useStripe();
  const elements = useElements();
  const [success, setSuccess] = useState(false)


  const PUBLIC_KEY = "pk_test_51OlUi6HkXpQM0uUWV0hKxwxs7L8mFEasFk3i2LC2vq70KUkHJn1jxWBSDRef2Wlfjkk5wLIjWEK24HvLMrrOJ4r400Fi5eNK7N"
  const stripePromise = loadStripe(PUBLIC_KEY)

  const handlePaymentSubmit = async (e) => {
    e.preventDefault()
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement)
    })

    if (!error) {
      try {
        const { id } = paymentMethod
        const response = await axios.post("http://localhost:4000/payment", {
          amount: actionData.price,
          id
        })

        if (response.data.success) {
          console.log("Successful payment")
          setSuccess(true)
        }

      } catch (error) {
        console.log("Error", error)
      }
    } else {
      console.log(error.message)
    }
  }



  const handleReset = () => {
    setFormData({
      number: '',
      name: '',
      expiry: '',
      cvc: ''
    });
    console.log("ok")
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  ///////////////////////////////////////////////////////


  return (
    <>
      {actionData && !actionData.message &&
        <Modal
          isOpen={actionData.pay_type === "card" && actionData && !actionData.message && modalOpen}
          onRequestClose={closeModal}>
          <Zoom>
            <button style={{ backgroundColor: "red" }} onClick={closeModal}>
              x
            </button>

            <div className="order-details">







              {/* ///////////////////////////////////////////////////////////////////////////////////////////////////////////// */}
              <Elements stripe={stripePromise}>
                {!success ?
                  <form>
                    <Styles>
                      <br />
                      <br />
                      <br />
                      <h1>🏁 Enter Card details</h1>
                      <a href="https://dashboard.stripe.com/test/developers">
                        Stripe
                      </a>
                      <iframe
                        className="card2024"
                        src="https://my.spline.design/creditcard-d88eb15d40c59aa8fee2992a1fccf38b/"
                        width={2000}
                        height={450}>
                      </iframe>
                      <div >
                        <h2>Total:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{actionData.price} {propertyData.property.currency}</h2>
                        <li style={{ borderBottom: "1px solid #ddd", listStyleType: "none" }} />
                        <br />
                      </div>
                      <div>
                        <label>Card Number&nbsp;</label>
                        <input
                          name="number"
                          value={formData.number}
                          component="input"
                          type="text"
                          onChange={handleInputChange}
                          pattern="[\d| ]{16,22}"
                          placeholder="1234 1234 1234 1234"
                          format={formatCreditCardNumber}
                        />
                      </div>(" ")
                      <div>
                        <label>Full name&nbsp;</label>
                        <input
                          name="name"
                          value={formData.name}
                          component="input"
                          type="text"
                          onChange={handleInputChange}
                        />
                      </div>(" ")
                      <div>
                        <label>Card Expiration&nbsp;</label>
                        <input
                          name="expiry"
                          value={formData.expiry}
                          component="input"
                          type="text"
                          pattern="\d\d/\d\d"
                          placeholder="MM / YY"
                          onChange={handleInputChange}
                          format={formatExpirationDate}
                        />
                        <label>&nbsp;&nbsp;&nbsp;CVC&nbsp;</label>
                        <input
                          name="cvc"
                          value={formData.cvc}
                          component="input"
                          type="text"
                          pattern="\d{3,4}"
                          placeholder="CVC"
                          onChange={handleInputChange}
                          format={formatCVC}
                        />
                      </div>(" ")


                      <fieldset className="FormGroup">
                        <div className="FormRow">
                          <CardElement options={CARD_OPTIONS} />
                        </div>
                      </fieldset>
                      <div className="buttons">
                        <button type="submit" style={{ backgroundColor: "blue" }} onClick={handlePaymentSubmit}>Pay</button>
                        <button
                          type="button"
                          onClick={handleReset}
                          style={{ backgroundColor: "grey" }}
                        >Reset</button>
                      </div>(" ")

                    </Styles>
                  </form>
                  :
                  <div>
                    <h2>You bought a property!</h2>
                  </div>
                }
              </Elements>
              {/* ///////////////////////////////////////////////////////////////////////////////////////////////////////////// */}

            </div>
          </Zoom>
        </Modal>
      }




      {actionData && !actionData.message &&
        <Modal
          isOpen={actionData.pay_type === "cash" && actionData && !actionData.message && modalOpen}
          onRequestClose={closeModal}>
          <Zoom>
            <button style={{ backgroundColor: "red" }} onClick={closeModal}>
              x
            </button>

            <div className="order-details">
              <h3 className="success-message">Your order has been placed</h3>
              <h2>Order {actionData._id}</h2>
              <ul>
                <li>
                  <div>Name:</div>
                  <div>{actionData.nume}</div>
                </li>
                <li>
                  <div>Perioud:</div>
                  <div>{actionData.data_start} - {actionData.data_end}</div>
                </li>
                <li>
                  <div>Price:</div>
                  <div>{actionData.price}</div>
                </li>

              </ul>
            </div>
          </Zoom>
        </Modal>
      }
      <BookForm />
    </>
  );
};





export const action = async ({ request, params }) => {
  const propertyId = params.propertyId;
  const data = await request.formData();

  const data_start = data.get("data_start");
  const data_end = data.get("data_end");
  // console.log(data_start, data_end, propertyId);


  let saleData = {
    placeId: propertyId,
    data_start: data.get("data_start"),
    data_end: data.get("data_end"),
    nume: data.get("nume"),
    adresa: data.get("adresa"),
    telefon: data.get("telefon"),
    pay_type: data.get("pay_type"),

  };

  const token = getAuthToken();

  const response = await fetch("http://localhost:8000/sale", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + token,
    },
    body: JSON.stringify(saleData),
  });


  if (response.status === 422 || response.status === 401) {
    return response;
  }

  if (!response.ok) {
    throw json({ message: "Could not authenticate user." }, { status: 500 });
  }

  return response;

}

export default BookPage;
