import Rating from "react-rating";
import starRed from "./assets/star-red.png";
import starGrey from "./assets/star-grey.png";
import starGold from "./assets/star-yellow.png";
import { useState } from "react";
import { getAuthToken } from "../utils/auth";


const BookList = ({ reservations }) => {
    const [showPopup, setShowPopup] = useState(false);
    const [comments, setComments] = useState(false);


    const onRateHandler = async (id, rate) => {
        const token = getAuthToken();

        const response = await fetch(`http://localhost:8000/sale/${id}`, {
            method: "PUT",
            headers: {
                Authorization: "Bearer " + token,
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ rating: rate }),
        })
    }


    const changePopupState = () => {
        setShowPopup(!showPopup);
    };

    const onCommentHandler = async (id, comment) => {
        const token = getAuthToken();

        const response = await fetch(`http://localhost:8000/sale/${id}`, {
            method: "PUT",
            headers: {
                Authorization: "Bearer " + token,
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ rating: comment }),
        })
    };






    const submitComment = (id, comment) => {
        setComments(prevComments => {
            const updatedComments = { ...prevComments }; 
            updatedComments[id] = updatedComments[id] ? [...updatedComments[id], comment] : [comment]; 
            return updatedComments; 
        });
    };
    
    return (
        <>
            <ul>
                <h1>My rezervations:</h1>
                {
                    reservations.map(rez => {
                        return (
                            <>
                                <li style={{ padding: "8px 16px", borderBottom: "1px solid #ddd", listStyleType: "none" }}>
                                    <p>Country: {rez.tara}, City: {rez.oras}, Surface:{rez.suprafata}, </p>
                                    <p>start: {rez.data_start}, end: {rez.data_end}</p>
                                    <p>owner: {rez.owner}</p>
                                    <p>Total price: {rez.price} {rez.currency} </p>
                                </li>
                                <Rating
                                    placeholderRating={rez.rating}
                                    emptySymbol={
                                        <img src={starGrey} className="icon" />
                                    }
                                    placeholderSymbol={
                                        <img src={starRed} className="icon" />
                                    }
                                    fullSymbol={
                                        <img src={starGold} className="icon" />
                                    }
                                    onChange={(rate) => { onRateHandler(rez._id, rate) }}
                                />


                                <button onClick={changePopupState}
                                    type="button"
                                    id="images"
                                    style={{ color: "green" }}>
                                    Give comment
                                </button>
                              


                                {showPopup && <PopupWindow onClose={(comment) => { changePopupState(); submitComment(rez._id, comment); }} />}
                                
                                <ul>
                                <h2>All Comments:</h2>
                                    {Object.keys(comments).map(reservationId => (
                                        comments[reservationId].map((comment, index) => (
                                            <li key={`${reservationId}-${index}`}>{comment}</li>
                                        ))
                                    ))}
                                </ul>

                             </>
                            )
                     })
                }
            </ul >
        </>
    )
}

function PopupWindow(props) {
    const [isComment, setIsComment] = useState("");

    const handleChange = (e) => {
        setIsComment(e.target.value);
    };

    const handleSubmit = () => {
        window.confirm(`Submitted comment: ${isComment}`);
        setIsComment('');
        props.onClose(isComment);
    };



    return (
        <div className="popup">
            <div className="popup-inner">
                <h2>Leave us a comment</h2>
                <textarea
                    value={isComment}
                    onChange={handleChange}
                    placeholder="Write your opinion here..."
                    rows={6}
                    cols={40}
                />
            </div>
            <button onClick={handleSubmit} style={{ color: "green" }}>Submit</button>
        </div>
    );
}

export default BookList;
