import { Form, useActionData, useNavigation } from "react-router-dom";
import classes from "./ProfileForm.module.css";
import { useState } from "react";

const ProfileForm = ({ user }) => {
  const [addCategoryData, setAddCategoryData] = useState(false);
  const [categoryName, setCategoryName] = useState('');
  const [category, setCategory] = useState(false);


  const navigation = useNavigation();
  const data = useActionData();

  const [showFilePreview, setShowFilePreview] = useState(false);
  const isSubmitting = navigation.state === "submitting";

  const imageChangeHandler = (event) => {
    console.log(event.target.id);
    const file = event.target.files;

    let previewId = "file-preview";

    if (file) {
      const fileReader = new FileReader();
      //console.log(file);
      const preview = document.getElementById(previewId);
      fileReader.onload = (event) => {
        preview.setAttribute("src", event.target.result);
      };

      console.log(fileReader);
      if (file[0]) {
        fileReader.readAsDataURL(file[0]);
        setShowFilePreview(true)
      } else {
        preview.setAttribute("src", null);
        setShowFilePreview(false)
      }
    }
  };

  const clearImageFromInput = (event) => {
    console.log("clearimageFromInput");
    console.log(event.target.id);
    const fileInput = document.getElementById(event.target.id);

    let previewId = "file-preview";

    const preview = document.getElementById(previewId);

    preview.setAttribute("src", null);
    fileInput.value = null;
    setShowFilePreview(false)
  };




  ////////////////////////////////////////////////////////
  const onChangeCategoryHandler = async (event) => {
    setCategory(event.target.value);
  }

  const submitCategoryHandler = async (id) => {
    const token = getAuthToken();

    const response = await fetch(`http://localhost:8000/addCategory`, {
      method: "POST",
      headers: {
        Authorization: "Bearer " + token,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ category: category }),
    })

    user = user.map(cat => {//takes the values
      if (cat._id === id) {
        cat.category = category;
        return cat;
      } else {
        return cat;
      }
    })
    setAddCategoryData(false);

  };

  /////////////////////////////////////////////////////



  return (
    <>
      <Form
        method="patch"
        className={classes.form}
        encType="multipart/form-data"
      >
        {data && data.message && <p>{data.message}</p>}
        {data && data.data && (
          <>
            {data.data.map((err) => {
              console.log("---", err);
              return <p key={err.msg}>{err}</p>;
            })}
          </>
        )}
        <p>
          {user && user.image && (
            <>
              <img
                height={150}
                width={150}
                src={`http://localhost:8000/${user.image}`}
              />

            </>
          )}
        </p>
        <p>{user.email}</p>
        <p>
          <label htmlFor="name">Name</label>
          <input id="name" type="text" name="name" defaultValue={user.name} />
        </p>

        <p>
          <label htmlFor="images">Images</label>
          <>
            <input
              id="images"
              type="file"
              name="images"
              onChange={imageChangeHandler}
            />
            <div>
              <img
                src="#"
                height={150}
                width={150}
                alt="Preview Uploaded Image"
                id="file-preview"
                style={{ marginRight: "20px" }}
                hidden={!showFilePreview}
              ></img>
              {showFilePreview && <button
                type="button"
                id="images"
                onClick={clearImageFromInput}
                style={{ color: "green" }}
              >
                Clear
              </button>}
            </div>
          </>
        </p>
        <div className={classes.actions}>
          <button disabled={isSubmitting}>
            {isSubmitting ? "Submitting" : "Save"}
          </button>
        </div>
      </Form>






      <button style={{ color: "black", position: "absolute", top: "90%", left: "72%", border: "1px solid green" }}
        onClick={() => {
          setAddCategoryData(!addCategoryData);
        }}
      >Add category</button>
      {data && data.message && <p>{data.message}</p>}
      {data && data.data && (
        <>
          {data.data.map((err) => {
            console.log("---", err);
            return (
              <p key={err.msg}>{err.msg}</p>
            )
          })}
        </>
      )}
      {addCategoryData && (
        <div
          style={{ //position: "absolute",top: "100%",left: "70%",
            border: "1px solid black",
            padding: "20px",
          }}
        > <h2>Enter category name</h2>
          <textarea
            onChange={onChangeCategoryHandler}
            type="text"
            value={categoryName}
            placeholder="abc..."
            style={{ marginBottom: "10px" }}
          />
          <div className={classes.actions}>

            <button onClick={submitCategoryHandler} style={{ color: "green" }}>Submit</button>
          </div>
        </div>
      )}
      {user.category && <>{user.category}</>}


      {showCommentPopup && showCommentPopupRezId === rez._id && <div className="popup">
        <div className="popup-inner">
          <h2>Leave us a comment</h2>
          <textarea
            id={rez._id}
            onChange={onChangeCommentHandler}
            placeholder="Write your opinion here..."
            rows={6}
            cols={40}
          />
        </div>
        <button onClick={() => { submitCommentHandler(rez._id) }} style={{ color: "green" }}>Submit</button>
      </div>}
      {rez.comment && <>{rez.comment}</>}

    </>
  );
};

export default ProfileForm;
