import React, { useState } from "react";
import { FaBars } from "react-icons/fa";
import classes from "./MenuStructure.module.css";

const MenuStructure = () => {
  const [isNavOpen, setIsNavOpen] = useState(false);

  const toggleNav = () => {
    setIsNavOpen(!isNavOpen);
  };

  const handleMouseEnter = () => {
    setIsNavOpen(true);
  };

  const handleMouseLeave = () => {
    setIsNavOpen(false);
  };

  return (
    <header
      className={`${classes.header} ${isNavOpen ? classes.responsive_nav : ""}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <h3>Logo</h3>
      <button className={`nav_btn ${isNavOpen ? classes.nav_close : ""}`} onClick={toggleNav}>
        {isNavOpen ? <FaBars style={{ visibility: "hidden" }} /> : <FaBars />}
      </button>
      {isNavOpen && (
        <nav className={classes.nav}>
          <a href="/#">Profile</a>
          <a href="/#">Language</a>
          <a href="/#">Help</a>
          <a href="/#">Logout</a>
          <a href="/#">About us</a>
        </nav>
      )}
    </header>
  );
};

export default MenuStructure;
